    public static ArrayList<RecyclerViewAllQuotesData> getTimeQuotes() {
        ArrayList<RecyclerViewAllQuotesData> quotesList = new ArrayList<>();
        ArrayList<String> list = addTimeQuotes();
        int i = 0;
        while (i < list.size()) {
            String ch = list.get(i);
            String[] fa = ch.split("2");
            quotesList.add(new RecyclerViewAllQuotesData(Integer.toString(i), fa[0], fa[1]));
            i++;
        }
        return quotesList;
    }


